// inner 内半径，outer外半径，a内部幂指数，b外部幂指数。
float myStep(float inner,float outer,float a,float b,float dist){
    if (.0<=dist && dist<inner){
        return pow((-dist+inner),a);
    }
    else if (inner<=dist && dist<=outer){
        return 0.0;
    }else{
        return pow((dist-outer),b);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.xy;
    float aspect = 1280.0 / 720.0; 
    st.x *= aspect;

    vec3 col = vec3(1.0);
    float pct = 0.0;
    float d = distance(st,vec2(0.5*aspect,0.5));
    // inner 内半径，outer外半径，a内部幂指数，b外部幂指数。
    float radius = myStep(.3,.32,0.4,0.4,d);

    col = radius*col;

    // Output to screen
    fragColor = vec4(col,1.0);
}
