//fork of https://www.shadertoy.com/view/ftKBzt by gaz
//gaz is the originator and master of this kind of
//luminous fractal

#define R(p,a,t) mix(a*dot(p,a),p,cos(t))+sin(t)*cross(p,a)
#define H(h)  (cos( max(1.,(1.1+sin(t)))*1.3*h +vec3(5,25,21) )*.7 + .2 )
void mainImage( out vec4 O, vec2 C)
{ 
    O=vec4(0.,0.,0.,1.);
    vec3 r=iResolution,c=vec3(0),
    d = normalize(vec3(C-.5*r.xy,r.y))*4.;
    float s,e,g=0.,t=iTime;
	for(float i=0.;i<115.;i++){
        vec4 p=vec4(g*d,0.);
        p.xyz=R(p.xyz+vec3(0.,0.,-2.5),normalize(H(t*.05)),t);
        s=1.;
        for(float j=0.;j<7.;j++) {  
            p= abs(p)*.6208;
            s*=e=max(1./dot(p,p),.3),
            p=abs(p.x<p.y?p.wzxy:p.wzyx)*e - 1.1;  
        }
        g+=e=abs(length(p.yzw*p.x)-.0)/s+.2e-4;
	    c+=mix(vec3(1),H(log(1.+abs(s))),.7)*.015/exp(i*i*e*e);
	}
	c*=c*c;
    c=1.-exp(-c);
    O=clamp(  vec4(c,1.),0.,1.);
    O=sqrt(O);
}
