void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    float x=uv.x, y=uv.y;
    float time = mod(iTime, 100.0);


    vec3 color = vec3(0.0, 0.0, 0.0);

    float amplitude = 0.13 * sin(time * 1.119 * x * y);
    y += amplitude * sin(x * 11.0 + iTime * 4.0);

    color.r = pow(abs(y - 0.5) * 7.0 + 0.3, 2.5);

    color.r = 1.0 - color.r;

    if (color.r >= 1.0) {
        color.r = 0.0;
    }

    color.b = 0.2 * pow(1.0 - color.r, 2.0);


    fragColor = vec4(color,1.0);
}
