void mainImage( out vec4 c, vec2 p )
{
    vec2 uv = (p.xy / iResolution.xy) * 2. - 1.;
    uv.x *= iResolution.x / iResolution.y;
    float r = max( 0.,  1.3 - length(uv) );
    float t = iTime * 1.0;
    if (iMouse.x <= 0.) t = 10.;
    t = r * r * sin(r+t) * 3.0;

    // Use a combination of mathematical functions to create a colorful pattern
    float r1 = sin(uv.x * 10.0 + t) * 0.5 + 0.5;
    float g1 = cos(uv.y * 10.0 + t) * 0.5 + 0.5;
    float b1 = tan(uv.x * uv.y + t) * 0.5 + 0.5;

    // Use the mod function to create a repeating pattern
    float r2 = mod(uv.x * 50.0 + t, 2.0);
    float g2 = mod(uv.y * 50.0 + t, 2.0);
    float b2 = mod(uv.x * uv.y + t, 2.0);

    // Combine the two color patterns and rotate them over time
    vec3 color = vec3(r1, g1, b1) + vec3(r2, g2, b2);
    mat3 rotationMatrix = mat3( cos(t), -sin(t), 0,
                                sin(t), cos(t), 0,
                                0, 0, 1);
    color = rotationMatrix * color;

    c = vec4(color, 1.0);
}
