void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float value = acos(uv.x * 5.0 - 2.5);
    float display = step(value, uv.y * 10.0 - 5.0);

    // Output to screen
    fragColor = vec4(display, display, display, 1.0);
}
