vec3 GMDN(in vec3 c){
    float scale = 1.;
    c *= scale;
    float arithmean = c.r + c.g + c.b;
    arithmean /= 3.;
    float geomean = c.r * c.b *c.g; 
    geomean = pow(geomean, (1./3.));
    float median = c.r + c.g + c.b - min(min(c.r,c.g),c.b) - max(max(c.r,c.g),c.b);
    c=vec3(median, geomean, arithmean);
    c /= scale;
    return c; 
}
vec3 rgb2hsv( in vec3 c ){
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz),
                 vec4(c.gb, K.xy),
                 step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r),
                 vec4(c.r, p.yzx),
                 step(p.x, c.r));
    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)),
                 d / (q.x + e),
                q.x);
}
