vec3 hsv2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord.xy / iResolution.xy;
    //uv.x = 1.0 - uv.x; /*Horizontal mirror*/
    vec3 col;
    col= tanh(1.5*vec3(
        mix(
            mix(
                hsv2rgb(vec3(
                    rgb2hsv(cross(
                        texture(iChannel0,uv).xyz,
                        GMDN(texture(iChannel0,uv).xyz))).x,
                    rgb2hsv(cross(
                        texture(iChannel0,uv).xyz,
                        GMDN(texture(iChannel0,uv).xyz))).y*.25,
                    rgb2hsv(cross(
                        texture(iChannel0,uv).xyz,
                        GMDN(texture(iChannel0,uv).xyz))).z+.5)),
                texture(iChannel0,uv).xyz,
                .5),
            GMDN(mix(
                hsv2rgb(vec3(
                    rgb2hsv(cross(
                        texture(iChannel0,uv).xyz,
                        GMDN(texture(iChannel0,uv).xyz))).x,
                    rgb2hsv(cross(
                        texture(iChannel0,uv).xyz,
                        GMDN(texture(iChannel0,uv).xyz))).y*.5,
                    rgb2hsv(cross(
                        texture(iChannel0,uv).xyz,
                        GMDN(texture(iChannel0,uv).xyz))).z+.5)),
                texture(iChannel0,uv).xyz,
                .5)),
            2.)));
    col=mix(col,tanh(mix(col,mix(texture(iChannel3,uv).xyz,mix(texture(iChannel1,uv).xyz,texture(iChannel2,uv).xyz,.5),.5),.5)),iMouse.x/iResolution.x);
    fragColor = vec4(col,1.0);
}

