void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy*.5)/iResolution.y;     

    float l = length(uv);

    float t = iTime;
    float d = floor(l*10. - t)/10. + t/10. + l/3.;

    vec3 col = vec3(1. - d);

    fragColor = vec4(col,1.0);
}
