float saw(float t, float f) {
	return fract(t * f);
}

float saw_med(float t) {
    float y = fract(t/16.0)*16.0;
    if (y < 4.0) return 110.0;
    if (y < 8.0) return 88.0;
    if (y < 12.0) return 99.0;
    if (y < 16.0) return 110.0;
    return 0.0;
}

vec2 mainSound( float time ) {
    float melody = saw_med(time);
    float wave = saw(time, melody);
    float b = melody * 0.5;
    float c = wave + saw(time, b);
    float volume = 0.2;
    float d = c * fract(-time * 4.0) * volume;
    return vec2(d);
}

vec3 saw_med_color(float t) {
    float y = fract(t/16.0)*16.0;
    if (y < 4.0)  return vec3(1., 0., 0.);
    if (y < 8.0)  return vec3(0., 1., 0.);
    if (y < 12.0) return vec3(0., 0., 1.);
    if (y < 16.0) return vec3(1., 1., 1.);
    return vec3(0.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    vec2 ss = mainSound(iTime);
    vec3 col = vec3(ss.x);
    col *= saw_med_color(iTime);
    fragColor = vec4(col , 1.0);
}

