mat2 rotate2d(float _angle) {
    return mat2(cos(_angle), -sin(_angle), sin(_angle),cos(_angle));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;

    st -= vec2(0.9,0.5);


    st *= rotate2d(sin(iTime * 3.5 + st.x ));

    float lines = fract(st.x * 10.0) * mod(2.0,abs(st.x + sin(iTime * 1.3) + 0.1 / st.y));

    lines = smoothstep(0.1,0.3,lines);    

    float mask = step(0.45,length(st));


    vec3 color2 = mix(vec3(0.059,0.995,0.104),vec3(0.995,0.168,0.063),vec3(lines));
    color2 *= lines;

    // Output to screen
    fragColor = vec4(color2,1.0);
}
