void mainImage(out vec4 c, vec2 u)
{
    c = .6+.8*texelFetch(iChannel0, ivec2(u),0);
    //c = .5+.5*texelFetch(iChannel0, ivec2(u),0);
    c *= vec4( 1.3, 0.9, 1.2, 1. ); // Pritty and pink

    c *= (texelFetch(iChannel1, ivec2(u),0).g*1.3)+0.125;


    vec2 q = u / iResolution.xy;
    c *= 0.4+0.5*pow(16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.5)*3.;
}

vec4 Tex( sampler2D tex, vec2 uv ) {
    return texture(tex, fract((uv)/iResolution.xy));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    fragColor=1./fragCoord.yyyx;
    for(float t=.6; t<820.; t+=t){
    	fragColor += fragColor.gbar/4.-fragColor*.3+Tex(iChannel0, fragCoord.xy-fragColor.wz*t);
    }

    //float v = texelFetch( iChannel1, ivec2(int(uv.x*512.0),0), 0 ).x;
    float v = texelFetch( iChannel1, ivec2(64,0), 0 ).g*0.6;



	fragColor = mix(Tex(iChannel0, fragCoord.xy+fragColor.xy), cos(fragColor), v-0.02);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float o = texelFetch( iChannel0, ivec2(0,0), 0 ).x;
    float v = texelFetch( iChannel1, ivec2(64,0), 0 ).x;

    o = mix(o,v, 0.001);

    v = ((v-o)*1.5)+((v*0.6));

    fragColor = vec4(o, v, 0.,1.);
}
