// Fork of "Quad Mapping Tool" by vamoss. https://shadertoy.com/view/Xd3SR8
// 2021-05-29 12:04:16

//store the buffer drags values
vec2 _Values[6]; //총 6개 핸들


//drags from buffer
//buffer variables
const int maxCount = 6;//총 6개 핸들 포인트
const float radius = 10.0;
const float border = 1.5;

const float statePos = float(maxCount);
#define BufA(x) texture(iChannel0, (vec2(x, 0.0) + 0.5) / iResolution.xy)

// From: Iq //https://iquilezles.org/articles/ibilinear
// https://www.shadertoy.com/view/lsBSDm
// given a point p and a quad defined by four points {a,b,c,d}, return the bilinear
// coordinates of p in the quad. Returns (-1,-1) if the point is outside of the quad.
float cross2( in vec2 a, in vec2 b ) { return a.x*b.y - a.y*b.x; }

vec2 invBilinear( in vec2 p, in vec2 a, in vec2 acb, in vec2 b, in vec2 c,in vec2 ccd, in vec2 d )
{

    vec2 e = b-a;//_
    vec2 f = d-a;//|
    vec2 g = a-b+c-d;
    vec2 h = p-a;

    float k2 = cross2( g, f );
    float k1 = cross2( e, f ) + cross2( h, g );
    float k0 = cross2( h, e );

    float k2u = cross2( e, g );
    float k1u = cross2( e, f ) + cross2( g, h );
    float k0u = cross2( h, f);    

    float v1, u1, v2, u2;

    if (abs(k2) < 1e-5) 
    {
        v1 = -k0 / k1;
        u1 = (h.x - f.x*v1)/(e.x + g.x*v1);
    } 
    else if (abs(k2u) < 1e-5) 
    {
        u1 = k0u / k1u;
        v1 = (h.y - e.y*u1)/(f.y + g.y*u1);
    } 
    else 
    {
        float w = k1*k1 - 4.0*k0*k2;

        if( w<0.0 ) return vec2(-1.0);

        w = sqrt( w );

        v1 = (-k1 - w)/(2.0*k2);
        v2 = (-k1 + w)/(2.0*k2);
        u1 = (-k1u - w)/(2.0*k2u);
        u2 = (-k1u + w)/(2.0*k2u);
    }
    bool  b1 = v1>0.0 && v1<1.0 && u1>0.0 && u1<1.0;
    bool  b2 = v2>0.0 && v2<1.0 && u2>0.0 && u2<1.0;

    vec2 res = vec2(-1.0);

    if(  b1/* && !b2 */) res = vec2( u1, v1 );
    if( /*!b1 &&  */b2 ) res = vec2( u2, v2 );

    return res;
}

float line(vec2 uv, vec2 origin, vec2 destiny, float radius){
    destiny -= origin;
	float color = length( clamp( dot(uv-origin,destiny)/dot(destiny,destiny), 0.,1.) *destiny - uv+origin );
    return smoothstep(color-0.0005,color+0.0005, radius);    
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    //read drag buffer
    vec4 state = BufA(statePos);
    float curr = 0.0;
    float dist = radius + 1.0;
    for (int i = 0; i < maxCount; ++i) {
        float f = float(i);
        if (f >= state.y)
            break;
        vec2 center = BufA(f).xy;
        float d = distance(fragCoord, center);
        if (d < dist) {
            dist = d;
            curr = f;
        }
    }
    for (int i = 0; i < maxCount; i++) {
        float f = float(i);
        if (f >= state.y)
            break;
        vec2 center = BufA(f).xy;

        //store drag values
        _Values[i] = center/iResolution.xy;
    }

    //distort
    vec3 color = vec3(0.0);//background
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 texUv = invBilinear( uv, _Values[0], _Values[1], _Values[2], _Values[3], _Values[4], _Values[5] );
    if( texUv.x>-0.5 )
    {
        color = texture( iChannel1, texUv ).xyz;
    }

    //line border
   	//*
    color = mix(color, vec3(0.0, 1.0, 1.0), line(uv, _Values[0], _Values[1], .005));//좌하
    color = mix(color, vec3(1.0, 0.0, 1.0), line(uv, _Values[1], _Values[2], .005));//중하
    color = mix(color, vec3(1.0, 1.0, 0.0), line(uv, _Values[2], _Values[3], .005));
    color = mix(color, vec3(0.0, 1.0, 0.0), line(uv, _Values[3], _Values[4], .005));//우상
    color = mix(color, vec3(0.0, 0.5, 0.5), line(uv, _Values[4], _Values[5], .005));
    color = mix(color, vec3(0.0, 0.0, 1.0), line(uv, _Values[5], _Values[0], .005));//좌상
	/**/

    //drags
    //*
    if (dist < radius) 
    {
        if (dist > radius - border) {
            //border
            color = vec3(0.0, 0.0, 0.0);
        } 
        else 
        {
            if (curr == state.x){
                if (state.z > 0.0)
                    //mouse pressed
                    color = vec3(0.0, 1.0, 0.0);
                else
                    //last pressed
                    color = vec3(1.0, .0, .0);

            }else{
                //standby
                color = vec3(1.0, 1.0, 1.0);
                }
        }
    }
    /**/

    fragColor = vec4(color, 1.0);
}

// Drag and drop framework
// https://www.shadertoy.com/view/4dtSRn
// by mhnewman
//
// maxCount := the maximum number of objects that can be represented.
//
// Buffer pixels (0, 0) to (maxCount - 1, 0) := the coordinates of each object.
//  * .x := the object's x coordinate.
//  * .y := the object's y coordinate.
//
// Buffer pixel (maxCount, 0) := the current state.
//  * .x := the index of the currently selected object.
//  * .y := the number of active objects.
//  * .z := the mouse state.
//
// Buffer pixel (maxCount + 1, 0) := the mouse offset from the center of the current object.

const int maxCount = 6;
const float radius = 10.0;

const float statePos = float(maxCount);
const float offsetPos = float(maxCount + 1);

#define addObject(xPos, yPos) { if (p.x == count) fragColor = vec4((xPos), (yPos), 0.0, 0.0); count += 1.0; }

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 p = fragCoord - 0.5;

    if (p.y > 0.0 || p.x > offsetPos) {
        fragColor = vec4(0.0);
        return;
    }

    if (iFrame == 0) {
        float count = 0.0;

        addObject(0.2 * iResolution.x, 0.2 * iResolution.y);//좌하
        addObject(0.5 * iResolution.x, 0.2 * iResolution.y); //중하
        addObject(0.8 * iResolution.x, 0.2 * iResolution.y);
        addObject(0.8 * iResolution.x, 0.8 * iResolution.y);
        addObject(0.5 * iResolution.x, 0.8 * iResolution.y);//중상
        addObject(0.2 * iResolution.x, 0.8 * iResolution.y);//좌상

        if (p.x == statePos)
            fragColor = vec4(2.0 * statePos, count, 0.0, 0.0);
        return;
    }

    vec4 state = texture(iChannel0, vec2(0.5 + statePos, 0.5) / iResolution.xy);
    vec4 offset = texture(iChannel0, vec2(0.5 + offsetPos, 0.5) / iResolution.xy);

    if (state.z <= 0.0 && iMouse.z > 0.0) {
        float r = radius + 1.0;
        for (int i = 0; i < maxCount; ++i) {
            float f = float(i);
            if (f >= state.y)
                break;
            vec4 center = texture(iChannel0, vec2(0.5 + f, 0.5) / iResolution.xy);
            float d = distance(iMouse.xy, center.xy);
            if (d < r) {
                r = d;
                state.x = f;
                offset = center - iMouse;
            }
        }
        if (r > radius) {
            if (state.y >= statePos) {
            	state.x = 2.0 * statePos;
            } else {
    	        state.x = state.y;
        	    state.y += 1.0;
        	}
        }
    }
    state.z = iMouse.z;

    if (p.x == statePos) {
        fragColor = state;
        return;
    }

    if (p.x == offsetPos) {
        fragColor = offset;
        return;
    }

    if (p.x == state.x && state.z > 0.0) {
        fragColor = iMouse + offset;
        return;
    }

    fragColor = texture(iChannel0, fragCoord / iResolution.xy);
}
