const float PI = 3.14;

void mainImage( out vec4 O, in vec2 u )
{
    vec2 Res = iResolution.xy, 
        U = (2. * u - Res) / Res.y;
    float y = 0.2 * asin(sin(4. * U.x + 4. * sin(iTime))) * cos(iTime);
    O = vec4(smoothstep(0.02, 0., abs(U.y - y)) + smoothstep(0.02, 0., abs(U.y + y)));
}
