#define tf texelFetch
const float pi=4.*atan(1.);


const float m1=	1.;
const float m2= 1.;

const float l1=	0.5;
const float l2=	0.5;

const float g=9.8;

const float dt=	1./60.;

bool keyPress(int ascii) { return (texture(iChannel1,vec2((.5+float(ascii))/256.,0.25)).x > 0.); }

vec4 acc(vec4 s){
	vec4 f;
	f.x = s.z*dt;
	f.y = s.w*dt;

    float d=s.x-s.y;
    float c1=cos(d);
    float s1=sin(d);

    float a=2.*m1+m2*(1.-cos(2.*d));

	f.z =-g*(2.*m1+m2)*sin(s.x)-m2*g*sin(s.x-2.*s.y)-2.*s1*m2*(s.w*s.w*l2+s.z*s.z*l1*c1);
	f.z/=l1*a;
    f.z*=dt;

    f.w =2.*s1*(s.z*s.z*l1*(m1+m2)+g*(m1+m2)*cos(s.x)+s.w*s.w*l2*m2*c1);
	f.w/=l2*a;
    f.w*=dt;

    return f;
}

void RK4(inout vec4 s){
	vec4 k1 = acc(s			);
	vec4 k2 = acc(s + k1*.5	);
	vec4 k3 = acc(s + k2*.5	);
	vec4 k4 = acc(s + k3	);
    vec4 d=(k1 + k4) / 6. + (k2 + k3) / 3.;
	s += d;
    s.xy = mod(s.xy + pi, 2. * pi) - pi;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec4 s=texelFetch(iChannel0,ivec2(fragCoord),0);
    RK4(s);
    if(iFrame==0||keyPress(32))s=vec4(2.*pi*fragCoord.xy/iResolution.xy-pi,0,0);
    fragColor=s;
}
