float Circle(vec2 uv, vec2 position, float r, float blur) 
    {
        float d = length(uv - position);
        float c = smoothstep(r, r-blur, d);
        return c;
    }

void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
    vec2 uv = fragCoord.xy / iResolution.xy; // координаты от 0 до 1    
    uv -= .5;  // центр круга теперь в центре экрана; координаты от -0.5 до 0.5
    uv.x *= (iResolution.x / iResolution.y); 
    // голова
    float c = Circle(uv, vec2(.0, .25), .22, .005);
    // глаза
    c -= Circle(uv, vec2(.1, .3), .05, .01);    
    c -= Circle(uv, vec2(-.1, .3), .05, .01);       
    c += Circle(uv, vec2(.1, .3), .025, .01);       
    c += Circle(uv, vec2(-.1, .3), .025, .01);       
    // уши 
    c -= Circle(uv, vec2(.4, .25), .18, .005);    
    c -= Circle(uv, vec2(-.4, .25), .18, .005); 
    c += Circle(uv, vec2(.4, .25), .20, .005);
    c += Circle(uv, vec2(-.4, .25), .20, .005);
    c += Circle(uv, vec2(.4, .25), .16, .005);       
    c += Circle(uv, vec2(-.4, .25), .16, .005);
    // тело
    c += Circle(uv, vec2(.0, -.10), .18, .005);
    //руки
    c += Circle(uv, vec2(.2, -.08), .08, .005);
    c += Circle(uv, vec2(-.2, -.08), .08, .005);
    //ноги
    c += Circle(uv, vec2(.10, -.3), .07, .005);
    c += Circle(uv, vec2(-.10, -.3), .07, .005);    

    fragColor = vec4(vec3(c), 0);
}
