vec3 RED = vec3(0.88, 0.22, 0.17);
vec3 BLUE = vec3(0., 0.04, 0.54);
vec3 BLACK = vec3(0.);
vec3 YELLOW = vec3(1., 0.72, 0.08);
vec3 GREEN = vec3(0., 0.47, 0.27);
vec3 WHITE = vec3(1.);

vec3 flag(vec2 uv) {
    float w = fwidth(uv.y)*3.;

    // Red and Blue
    vec3 col = mix(BLUE, RED, smoothstep(0.5, 0.5+w, uv.y));

    // Fold Horizontally
    float y = 0.5-abs(0.5-uv.y);

    // Diagonal on the left
    y -= min(uv.x-0.5, 0.);

    // White Stripe
    col = mix(col, WHITE, smoothstep(0.333, 0.333+w, y));

    // Green Stripe
    col = mix(col, GREEN, smoothstep(0.4, 0.4+w, y));

    // Yellow Stripe
    col = mix(col, YELLOW, smoothstep(0.6, 0.6+w, y));

    // Black
    col = mix(col, BLACK, smoothstep(0.667, 0.667+w, y));

    // cutoff top and bottom edges
    col *= smoothstep(w, 0., abs(uv.y-0.5)-0.5);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float t = uv.x*5.-iTime*2.+uv.y*2.;
    uv.y += sin(t)*0.05;

    vec3 col = flag(uv);
    col *= .7+cos(t)*.3;

    fragColor = vec4(col,1.0);
}
