#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,q,r=iResolution,
    d=normalize(vec3((C-.5*r.xy)/r.y,1));  
    for(float i=0.,s,e,g=0.;
        ++i<90.;
        O.xyz+=mix(vec3(1),H(length(p)*3.),.6)*.015*exp(-8.*i*i*e)
    )
    {
        p=g*d;
        p=R(p,vec3(.577),iTime*.1);
        p.z-=iTime;
        s=6.;
        p=abs(fract(p*.5)-.5);
        p=R(p,p/p,.3);
        for(int i=0;i++<8;)
            p=.92-abs(p-.81),
            s*=e=1.3/dot(p,p)-.03,
            p*=e;
        g+=e=(dot(p,p/p)-1.1)/s-.002;
    }
}
