    float Circle(float x, float y, float r)
    {
        return x*x+y*y-r;
    }
    float Ellips(float x, float y ,float a,float b, float r)
    {
        return (x/a*x/a)+(y/b*y/b)-r;
    }
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 p=1.0-2.0*fragCoord.xy/iResolution.xy;
     float x=p.x * iResolution.x/iResolution.y;
     float y=p.y; 

    float head=Ellips(x, y + .5, 0.6, .4, .6);

    float body = Ellips(x, y - .15, 0.4, .5, .7);

    float ear1=Circle(x - .6, y + .4, .05);
    float ear2=Circle(x + .55, y + .7, .05);

    float leg1=Ellips(x - .2, y - .7, .25, .4, .3);
    float leg2=Ellips(x + .2, y - .7, .25, .4, .3);

    float foot1=Ellips(x - .3, y - .9, .25, .1, .6);
    float foot2=Ellips(x + .3, y - .9, .25, .1, .6); 

    float hand1=Ellips(x + .35, y - .2, .1, .2,1.);
    float hand2=Ellips(x - .35, y - .2, .1, .2,1.); 

    float z=head;
     z=min(z,body);
     z=min(z,ear1);
     z=min(z,ear2);
     z=min(z,leg1);
     z=min(z,leg2);
     z=min(z,foot1);
     z=min(z,foot2);
     z=min(z,hand1);
     z=min(z,hand2);

    vec3 col=vec3(1.,1.,1.);
     if(z<0.) col.z=z; 
     else col.z = 1.;
     fragColor = vec4(col, 1.0 );   
}
