float PI = 3.1415926;
float _ins = 0.25;

float Split(vec2 uv, vec2 scale,float time)
{
 //   time *=10.0;
	//vec2 a = vec2(15, 22);
	vec2 st = uv * scale;

	vec2 id = floor(st);

	st = fract(st) - 0.5;


    ///set random time to start
    //time +=  fract(sin(id.x *33631.34+id.y * 2312.7)*73312.34);// * 6.283;

	float y = 0.0;
    ///set random pos
    //y += fract(sin(id.x *32161.34+id.y * 23132.7)*71332.34)/3.;
    st.y += y;


	vec2 p1 = vec2(0.0, 0);
	vec2 o1 = (st - p1) ;

	///Circle sqrt(1/2*1/2 + 1/2*1/2)
	 float d = length(o1)/sqrt(0.5);

	//Square
	//float d = max(abs(o1.x), abs(o1.y))/0.5f;


	float m1 = 0.0;//1.0*(1.0-d);//  lerp(1, 0.0, d);


	m1 = step(d, time);



	return m1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{



    vec2 uv = fragCoord/iResolution.xy;

    uv = uv*2.0 - 1.0;

    float cValue =   ( iTime-floor(iTime)) * 2.0 *PI;//  (sin(iTime)+1.0)*PI;

    float alpha = 0.0;
    if(cValue < PI)
    {

        vec2 startRot = vec2(1.0,0.0);
        float angle = dot(startRot,normalize(uv));
        vec3 c = cross(vec3(startRot.x,startRot.y,0),normalize(vec3(uv.x,uv.y,0)));// normalize(uv));



        alpha =  step(acos(angle),(cValue));

        if(c.z>0.0 )
        {

                alpha = 0.0;
        }

    }
    else
    {
        vec2 startRot = vec2(-1.0,0.0);
        float angle = dot(startRot,normalize(uv));
        vec3 c = cross(vec3(startRot.x,startRot.y,0),normalize(vec3(uv.x,uv.y,0)));// normalize(uv));



        alpha =  step(acos(angle),(cValue-PI));

        if(c.z>0.0 )
        {
                alpha = 1.0;
        }
    }



    fragColor = vec4(alpha,alpha,alpha,alpha);// mix( vec4(.1), texture(iChannel0, fragCoord/iResolution.xy), Distort );


}
