uvec4 decodeval32(uint varz) {
    return uvec4(varz>>24,(varz>>16)&0xffu,(varz>>8)&0xffu,(varz>>0)&0xffu);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=fragCoord/iResolution.xy;


    ivec2 ipx=ivec2(fragCoord);
    float resize=texelFetch(iChannel0,ivec2(iResolution.xy)-1,0).a;

    if(resize<-50.){
        fragColor=texelFetch(iChannel0,ipx,0);
        return;
    }

    if(ipx==ivec2(iResolution.xy)-1){
        fragColor=vec4(0.,0.,0.,-100.);
        return;
    }

    ivec2 idx=ivec2(uv*texture_pixel_size);


    vec4 c=vec4(0.);
    ivec2 shift=ivec2(0);
    ivec2 tidx=idx-shift;
    if((tidx.y<=image_0_size.y)&&(tidx.x<image_0_size.x)&&(tidx.x>=0)&&(tidx.y>0)){
        int local_idx=(image_0_size.y-(tidx.y%image_0_size.y))+(tidx.x%image_0_size.x)*image_0_size.y;
        c=vec4(decodeval32(image_0_palette[(decodeval32(image_0_palette_data[local_idx/8])[((local_idx-(local_idx/8)*8)/2)%4]>>(4*(1-(local_idx%2))))&0xfu]))/float(0xff);
    }

    shift=image_0_size;
    shift.y=0;
    tidx=idx-shift;
    if((tidx.y<=image_1_size.y)&&(tidx.x<image_1_size.x)&&(tidx.x>=0)&&(tidx.y>0)){
        int local_idx=(image_1_size.y-(tidx.y%image_1_size.y))+(tidx.x%image_1_size.x)*image_1_size.y;

        c=vec4(decodeval32(image_1_palette[((decodeval32(image_1_palette_data[local_idx/16])[((local_idx-(local_idx/16)*16)/4)%4])>>(6-2*(local_idx%4)))&0x3u]))/float(0xff);
    }

    shift=image_1_size;
    shift.x=0;
    tidx=idx-shift;
    if((tidx.y<=image_2_size.y)&&(tidx.x<image_2_size.x)&&(tidx.x>=0)&&(tidx.y>0)){
        int local_idx=(image_2_size.y-(tidx.y%image_2_size.y))+(tidx.x%image_2_size.x)*image_2_size.y;

        c=vec4(decodeval32(image_2_palette[((decodeval32(image_2_palette_data[local_idx/16])[((local_idx-(local_idx/16)*16)/4)%4])>>(6-2*(local_idx%4)))&0x3u]))/float(0xff);
    }

    shift=image_1_size+image_2_size;
    shift.x=0;
    tidx=idx-shift;
    if((tidx.y<=image_3_size.y)&&(tidx.x<image_3_size.x)&&(tidx.x>=0)&&(tidx.y>0)){
        int local_idx=(image_3_size.y-(tidx.y%image_3_size.y))+(tidx.x%image_3_size.x)*image_3_size.y;

        c=vec4(decodeval32(image_3_palette[((decodeval32(image_3_palette_data[local_idx/16])[((local_idx-(local_idx/16)*16)/4)%4])>>(6-2*(local_idx%4)))&0x3u]))/float(0xff);
    }


    fragColor = c;
}

