bool ParallelogramIntersect( in vec3 ro, in vec3 rd, in vec3 v0, in vec3 v1, in vec3 v2, in vec3 v3, out float tx, out vec3 norm)
{
    vec3 a = v0 - v1;
    vec3 b = v2 - v0;
    vec3 p = v0 - ro;
    vec3 n = cross( a, b );
    vec3 q = cross( rd, p );
    float i = 1.0/dot( rd, n );
    float u = dot( q, a )*i;
    float v = dot( q, b )*i;
    float t = dot( n, p )*i;
    tx=-1.;
    norm=vec3(0.,1.,0.);
    if( u<0.0 || u>1.0 || v<0.0 || v>1.0 ) return false;
    vec3 normx = vec3( t, u, v );
    if( normx.x>MIN_DIST && normx.x<MAX_DIST )
    {
    tx = normx.x;
    vec3 nor = normalize( cross(v2-v1,v1-v3) );
    norm = faceforward( nor, rd, nor );
    return true;
    }
    return false;
}


// using https://iquilezles.org/articles/filterableprocedurals

float filteredSquares( in vec2 p, in vec2 dpdx, in vec2 dpdy, float N)
{
    vec2 w = max(abs(dpdx), abs(dpdy));
    vec2 a = p + 0.5*w;
    vec2 b = p - 0.5*w;
    if(length(w)<=0.0)return 1.;
    vec2 i = (floor(a)+min(fract(a)*N,1.0)-
              floor(b)-min(fract(b)*N,1.0))/(N*w);
    return i.x*i.y;
}

float filteredCrosses( in vec2 p, in vec2 dpdx, in vec2 dpdy )
{
    const float N = 3.0;
    vec2 w = max(abs(dpdx), abs(dpdy));
    vec2 a = p + 0.5*w;
    vec2 b = p - 0.5*w;
    if(length(w)<=0.0)return 1.;
    vec2 i = (floor(a)+min(fract(a)*N,1.0)-
              floor(b)-min(fract(b)*N,1.0))/(N*w);
    return 1.0-i.x-i.y+2.0*i.x*i.y;
}

vec3 get_pixels( in vec2 p)
{
    //p.y*=.25;
    //p*=2.;
    vec2 tp,dpdx,dpdy;
    float n=3.;
    tp=p;
    tp+=0.5*1./n;
    dpdx=dFdx(tp); dpdy=dFdy(tp);
    float r = filteredSquares(tp, dpdx, dpdy, n);
    n=4.25;
    tp=p;
    tp+=(0.5)*1./n+vec2(0.,.5);
    dpdx=dFdx(tp); dpdy=dFdy(tp);
    float g = filteredSquares(tp, dpdx, dpdy, n);
    tp=p;
    tp+=(0.5)*1./n+vec2(0.5,.0);
    dpdx=dFdx(tp); dpdy=dFdy(tp);
    g += filteredSquares(tp, dpdx, dpdy, n);
    n=2.45;
    tp=p;
    tp+=(0.5)*1./n+0.5;
    dpdx=dFdx(tp); dpdy=dFdy(tp);
    float b = filteredSquares(tp, dpdx, dpdy, n);
    return vec3(r, g, b)*vec3(0.985,1.,.65)*7.5;
}

vec4 get_texture(vec2 p){
    vec2 op=p;
    p.x*=1./(iResolution.x/iResolution.y);
