void ParallelogramIntersectMin(vec3 ro, vec3 rd, in vec3 v0, in vec3 v1, in vec3 v2, inout bool result, 
                               inout HitInfo hit, int OBJ) {
    float tnew;
    vec3 normnew;
    vec3 v3 = v1 + v2 - v0;
    if (ParallelogramIntersect(ro, rd, v0, v1, v2, v3, tnew, normnew)) {
