    vec3 v0 = vec3(-1.,-1.,-iResolution.x/iResolution.y); 
    vec3 v1 =  vec3(1.,-1.,-iResolution.x/iResolution.y); 
    vec3 v2 =  vec3(-1.,-1.,iResolution.x/iResolution.y); 


    ParallelogramIntersectMin(ro, rd, v0, v1, v2, result, hit,1);


    return result;
}

const float eps = 1e-3;

vec3 getSkyColor( vec3 rd , vec3 lig) {
    vec3 col = mix(vec3(1),vec3(.5,.7,1), .5+.5*rd.y);
    float sun = clamp(dot(normalize(lig),rd), 0., 1.);
    col += vec3(1,.6,.1)*(pow(sun,4.) + 10.*pow(sun,64.));
    return col;
}

vec3 render(Ray r)
{
    vec3 col = vec3(0.0);
    vec3 objectcolor = vec3(1.0);
    vec3 mask = vec3(1.0);
    HitInfo hit;
    hit.color=vec4(0.);
    bgcol.rgb=getSkyColor(r.dir,normalize(l1Pos))*0.1;
    {
        if(minDist(r.pos, r.dir, hit)){
            objectcolor = hit.color.rgb;
            vec3 p = r.pos + r.dir * hit.t + hit.norm*eps;
            vec3 sunDir = normalize(l1Pos-p);
            //col = objectcolor;
            col = objectcolor * (vec3(max(dot(hit.norm,sunDir),0.0)) + 0.05);
        }else col = bgcol.rgb;
    }
    return col;
 }

