vec4 colorA = vec4(0.905, 0.445, 0.133, 1.0);
vec4 colorB = vec4(0.328, 0.209, 0.021, 1.0);

float tileSize = 0.01;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    float theta = iTime * 0.05;
    float x = (floor((uv.x * cos(theta) - uv.y * sin(theta)) / tileSize));
    float y = (floor((uv.x * sin(theta) + uv.y * cos(theta)) / tileSize));

    if ((mod((x + y), 4.0)) <= 1.0)
        fragColor = colorA;
    else
        fragColor = colorB;
}
