    float Circle(vec2 uv, vec2 position, float r, float blur) 
    {
        float d = length(uv - position);
         float c = smoothstep(r, r-blur, d);
          return c;
    }

void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{   
    // координаты от -1 до 1
    vec2 uv = fragCoord.xy / iResolution.xy;    
          // центр круга в центре экрана;
          uv -= .5;
           uv.x *= (iResolution.x / iResolution.y); 
           // Голова
      float c = Circle(uv, vec2(.0, .15), .2, .005);
             // Глаза
             c -= Circle(uv, vec2(.1, .15), .05, .01);    
             c -= Circle(uv, vec2(-.1, .15), .05, .01);
             // Внешняя часть ушей
             c += Circle(uv, vec2(.34, .2), .18, .005);
             c += Circle(uv, vec2(-.34, .2), .18, .005);
             // Внутренняя часть ушей 
             c -= Circle(uv, vec2(.34, .2), .10, .005);
             c -= Circle(uv, vec2(-.34, .2), .10, .005);
             // Тело
             c += Circle(uv, vec2(.0, -.18), .15, .005);
             // Руки
             c += Circle(uv, vec2(.2, -.19), .06, .005);
             c += Circle(uv, vec2(-.2, -.19), .06, .005);
             // Ноги
             c += Circle(uv, vec2(.13, -.35), .07, .005);
             c += Circle(uv, vec2(-.13, -.35), .07, .005);    
    // Вывод на экран
    fragColor = vec4(vec3(c), 1.0);
}
