float stepper(vec2 p){

    vec2 xy = vec2(abs(p.x),p.y);
    float x = xy.x;
    float y = xy.y;


    float scalex = 1.;
    float xF = cos(x)*abs(sin(iTime)) + abs(sin(iTime))*2.*pow(sin(pow(y,1./2.)),1.);
    float yF = y;

    float eps = 0.1;

    return smoothstep(xF,xF+eps,yF);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 R = iResolution.xy;
    vec2 uv = (fragCoord-R/2.)/R.y;
    uv *= 10.;


    float val = stepper(uv);

    vec3 col = vec3(val);



    // Output to screen
    fragColor = vec4(col,1.0);
}
