void mainImage( out vec4 O, in vec2 u ) {
  vec2 Res = iResolution.xy,
  U = 2. * mod(8. * u / Res.xy, 1.) - 1.;
  float chess = 0.3 * step(0.0, sqrt(U.x * U.y * sin(iTime * 44.)));
  float cone0 = exp((-length(U - vec2(0.5, 0.5)) + 0.1)/0.01);
  float cone1 = exp((-length(U - vec2(0.5, -0.5)) + 0.1)/0.01);
  float cone2 = exp((-length(U - vec2(-0.5, 0.5)) + 0.1)/0.01);
  float cone3 = exp((-length(U - vec2(-0.5, -0.5)) + 0.1)/0.01);
  vec4 texColor = texture(iChannel0, u / Res.xy);
  O = vec4(chess) + vec4(0., 0., cone0 + cone1 + cone2 + cone3, 1.) * (0.1 + chess) + texColor * 0.5;
}
