float sdfCircle(vec2 o, float r, vec2 uv){
    return distance(o, uv) - r;
}

vec4 coloredCircle(vec2 o, float r, vec4 col, vec2 uv){
    return vec4(col.rgb, mix(0.0, col.a, smoothstep(0.005, 0.0, sdfCircle(o, r, uv))));
}

//mixes 2 colors with opacity
vec4 mixrgba(vec4 s, vec4 d){
    float a = s.a + d.a*(1.0-s.a);
    if (a == 0.0){
        return vec4(0.0);
    } 
    vec3 c = s.rgb*s.a + d.rgb*d.a*(1.0-s.a);
    return vec4(c/a, a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    vec3 grid = mod(floor(uv.x*20.0), 2.0) + mod(floor(uv.y*20.0), 2.0) == 1.0 ? vec3(0.8) : vec3(1.0);
    vec4 col = vec4(grid, 1.0);

    vec4 circle1 = coloredCircle(vec2(0.5, 0.4), 0.2, vec4(1, 0.5, 0, 0.5), uv);
    vec4 circle2 = coloredCircle(vec2(0.5, 0.6), 0.2, vec4(0, 0.7, 0.7, 0.5), uv);

    col = mixrgba(circle1, mixrgba(circle2, col));

    fragColor = vec4(col);
}
