#define PI 3.1415926538

float getAngle(vec2 v1)
{
    //return atan(v1.x,v1.y) -atan(v2.x,v2.y);
    return mod( atan(v1.x,v1.y) -atan(1.,0.), PI*2.)/PI/2.; //0 ... TWOPI
    //return mod( atan(v1.x,v1.y) -atan(v2.x,v2.y), TWOPI) - PI; //-pi to +pi 
}

vec2 stepper(vec2 p){

    float a = (getAngle(p)-0.5)*PI*20.;
    float lenStrands = 10.;
    float d = length(p)*lenStrands*0.5-lenStrands;


    vec2 xy = vec2(p.x,p.y);
    float x = xy.x;
    float y = xy.y+3.;

    x = a;
    y = d;


    float xF = sin(pow(x,1.)/10.) + pow(y-3. + 0.*4.*abs(sin(x)),4.) - y ;

    xF = 3. + xF*1. ;

    float yF = y*sin(x*2.+cos(y*1.+iTime*0.)/1. + sin(x + y + iTime) + iTime*0. );

    float eps = 0.2;

    return vec2(smoothstep(xF,xF+eps,yF),xF);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 R = iResolution.xy;
    vec2 uv = (fragCoord-R/2.)/R.y;
    uv *= 10.;


    float a = getAngle(uv);



    vec2 vals = stepper(uv);

    vec3 col = vec3(vals.x);
    //col = vec3(a);



    // Output to screen
    fragColor = vec4(col,1.0);
}
