const float NUM_LINES = 4. ;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // 归一化 uv 坐标 [0., 1.]
    vec2 uv = fragCoord.xy / iResolution.xy;;

    vec4 img = vec4(texture(iChannel0, uv));
    //时间控制面积
    float progress = abs(sin(iTime * 0.75));
    float lineW = 1.0 / NUM_LINES;  ///< 一个分区的宽度
    float count = 0.;

//    if ( progress > uv.x)   ///< 水平的整体滑动
    {
        for (float i = 0.; i < 1.0; i += lineW) ///< 遍历分区
        {
            count += 1.;

            /// 检查 uv.x 是否落在区间内
            if ((uv.x > i) && (uv.x < i + lineW * progress)) ///< [i, i+ 1./NUM_LINES * progress]
            {
                img = vec4(texture(iChannel1, uv));
            }
        }
    }


    fragColor = img;
}
