// variant of https://shadertoy.com/view/7td3W7


void mainImage( out vec4 O, vec2 U )
{
        O = T(U/1.);
}

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    U -= .5;
    if (iFrame==0) {            // --- initialization
        O.x = U.x + U.y*R.x;    // each pixel represent one integer
        O.y = 0.;               // step count
        return;
    }
                                // --- Syracuse steps
    O = T(U);                   // previous state
    int i = int(O.x+.5);
    if ( i==1 ) return;         // already converged

    i = i/2*2==i ? i/2 : 3*i+1; // hearth of the model
    O.x = float(i);             // store current value
    O.y++;                      // increment steps as long as not converged

}

#define hue(v)  ( .6 + .6 * cos( 6.3*(v)  + vec4(0,23,21,0)  ) )

void mainImage( out vec4 O, vec2 U )
{
    U -= .5;
    O = T(U);
    vec4 T = texelFetch(iChannel1, ivec2(U.x,0),0); // add explored value to the graph
    if ( U.y == round(T.x ) )  O += hue(.1*T.y);    // hue = corresponding iteration
}
