#define TAU (6.283185307)
// i want to use hex codes like in image editing programs,
// so here's an improved macro by FabriceNeyret2
#define HEX(x) vec3( ( ivec3(x) >> ivec3(16,8,0) ) & 255 ) / 255.

vec3 color(float x){
    const int colorCount = 8;
    vec3[] c = vec3[](
        HEX(0x11151c),
        HEX(0x390099),
        HEX(0x9e0059),
        HEX(0xff0054),
        HEX(0xff5400),
        HEX(0xffbd00),
        HEX(0x127475),
        HEX(0x8fefff)
    );
    x *= float(colorCount);
    int lo = 1048576 + int(floor(x));

    return mix(
        c[lo % colorCount],
        c[(lo + 1) % colorCount],
        fract(x)
    );
}

float square(vec2 uv, float angle) {
    // rotation.
    // thank you FabriceNeyret for teaching me to git gud at rotating a 2D vector in glsl
    uv *= mat2(
        cos(angle), -sin(angle),
        sin(angle),  cos(angle)
    );
    return max(abs(uv.x), abs(uv.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  float time = fract(iTime / 3.0);
  // make the center of the canvas (0.0, 0.0) and
  // make the long edge of the canvas range from -1.0 to +1.0
  // we'll use the scale variable later for antialiasing bc it's the size of one pixel
  float scale = max(iResolution.x, iResolution.y);
  vec2 uv = (gl_FragCoord.xy - 0.50 * iResolution.xy) / scale;

  // i blend each square's color in order to antialias this shader
  // are you proud of me yet Fabrice
  vec3 col = vec3(0., 0., 0.);
  float opacity = 0.;
  float colOffset = 0.1;
  const float iters = 32.;
  float size = 0.002;
  float sizeMult = 1.2;
  float angleOffset = 0.12 * (2.5 + sin(time * TAU));
  // i know, loops in glsl code, ew right
  for (float i = 0.; i < iters; i++) {
      // subtract from size and multiply by scale to get
      // a nice "pixels from boundary" value
      float dist = (square(
          uv, -time * TAU + angleOffset * i
      ) - size) * scale;
      // for demonstration purposes only
      dist /= 8.;
      if ( // fully inside square
          dist < 0.
      ) {
          col += (1. - opacity) * color(time + colOffset * i);
          opacity = 1.;
      } else if ( // partially inside square
          dist < 1.
      ) {
          float newOpacity = max(0., (1. - dist) * (1. - opacity));
          col += newOpacity * color(time + colOffset * i);
          opacity += newOpacity;
      } else { // fully outside square
          // do nothing
      }
      // i'm not sure if this saves time for gpu calculation
      // but i'm doing it anyway because it pleases me:
      if (opacity >= 1.) {break;}

      size *= sizeMult; // better than calling pow()
  }

  // Output to screen
  fragColor = vec4(
    col, 1.0
  );
}
