float box(vec2 position, vec2 halfSize, float cornerRadius) {
   position = abs(position) - halfSize + cornerRadius;
   return length(max(position, 0.0)) + min(max(position.x, position.y), 0.0) - cornerRadius;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 position = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    vec2 halfSize = vec2(0.7, 0.4);
    float cornerRadius = ((cos(iTime + 1.0) + 1.0) * 0.5) * 0.4;
	float d = box(position, halfSize, cornerRadius);

    // same colorization that Inigo Quilez uses in his shaders
    vec3 color = vec3(1.0) - sign(d) * vec3(0.1, 0.4, 0.7); // base color
    color *= 1.0 - exp(-4.0 * abs(d)); // gradient
	color *= 0.8 + 0.2 * cos(120.0 * d); // ripples
    color = mix(color, vec3(1.0), 1.0 - smoothstep(0.0, 3.0, (abs(d) - 0.003) * iResolution.y));
    if (iMouse.z > 0.0) {
        vec2 mousePosition = (2.0 * iMouse.xy - iResolution.xy) / iResolution.y;
        float d = box(mousePosition, halfSize, cornerRadius);
        color = mix(color, vec3(1.0, 1.0, 0.0), 1.0 - smoothstep(0.0, 3.0, abs(length(position - mousePosition) - abs(d)) * iResolution.y - 1.0));
    }
    fragColor = vec4(color, 1.0);
}
