void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col;
    vec2 uv1 = ((fragCoord)*10.0-iResolution.xy)/iResolution.y/40.;
    uv1.y -= 2.3;
    uv1 += iTime/160.;
    for(int c=0;c<3;c++){
        vec2 uv = uv1;
        for(int i=0;i<6;i++)
        {
            uv = fract((uv.y+uv.x+uv)*(1.-col.y));
            uv *= (uv.x-uv.y);
            //col += col.yzx/16./16.;
            col[c] += (uv.y-uv.x);
        }

	}

    fragColor = vec4(fract(col),1.0);

}
