void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    float stepy1 = 5.23;
    float stepy2 = 3.2;

    float stepx1 = 3.70;
    float stepx2 = 2.32;

    float Ydist = sin(uv.y*stepy1-iTime*3.2)*0.2+sin(uv.y*stepy2+iTime*1.2)*0.3;

    float m=uv.y+sin((uv.x+Ydist)*stepx1+iTime*2.3)*0.3+sin((uv.x+Ydist)*stepx2-iTime*1.3)*0.5;

    float mR = sin(8.*m);
    float mG = sin(8.*m+1.9);
    float mB = sin(8.*m+3.4);

    // Output to screen
    fragColor = vec4(mR,mG,mB,1.0);
}
