// variant of https://shadertoy.com/view/sld3D7


#define hue(v)  ( .6 + .6 * cos( 6.3*(v)  + vec4(0,23,21,0)  ) )

void mainImage( out vec4 O, vec2 U )
{
    O = T(U/1.);
    O = hue(O.x);
 // O = vec4(O.z/10.);
}

#define hash(p) fract(sin((p-sin(floor(iTime)))*mat2(127.1,311.7, 269.5,183.3)) *43758.5453123)

float radius = .01;
                                     // --- random link within radius, seed s.
#define link(s) T (mod( U + radius*R*(hash(s+U)*2.-1.) , R ) ).x


void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    U -= .5;
    if (iFrame==0) {                 // --- initialization
        O.x = U.x + U.y*R.x;         // node label: start with its id
        return;
    }
                                     // --- propagation along the random network
    O = T(U);                        // previous step
    O.y = O.x;                       // keep previous value


    O.x = min(O.x, link() );         // propagate from random link. label = min(id)

    O.z = O.x==O.y ? O.z+1. : 0.;    // convergence detector
}
