float map(vec2 pos){
    return max(0.2,texture(iChannel0,pos).x);
}

vec2 calcNormal( in vec2 pos )
{
	vec2 eps = vec2( 0.001, 0.0 );
	vec2 nor = vec2(
	    map(pos+eps.xy) - map(pos-eps.xy),
	    map(pos+eps.yx) - map(pos-eps.yx));
	return normalize(nor+.0001);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;


    vec2 g=calcNormal(uv);

    vec3 l=(.3+.7*dot(g,normalize(vec2(.8,.2))))*vec3(.2,.4,.9);
    vec3 l2=(.3+.7*dot(g,normalize(vec2(.0,.6))))*vec3(.8,.6,.1);
   // vec2 r=reflect(g,vec2(.5,.5)-uv);
   // vec3 s=pow(dot(normalize(r),normalize(uv)),.9)*vec3(.8);
    fragColor=texture(iChannel1,uv+.01*g)*vec4(clamp(l+l2*.8,0.,1.),1.);
    vec4 c=texture(iChannel0,uv);
    fragColor = mix(vec4(.9,.1,.1,1.)*(.7+sin(iTime+uv.x*uv.y*4.)*.2)+fragColor,fragColor,c.x);
    vec4(vec3(c.x),1.0);


}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;


    if(iFrame<3)
        fragColor=vec4(1.,0.,0.,1.);
    else if(length(iMouse.xy/iResolution.xy-uv)<.03)
            fragColor=vec4(0.,1.,0.,1.);
    else{
        float offset=1./iResolution.y;

        fragColor=computeSim(uv,iChannel0,offset);
    }

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    float offset=1./iResolution.y;

    fragColor=computeSim(uv,iChannel0,offset);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
