void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    float offset=1./iResolution.y;

    fragColor=computeSim(uv,iChannel0,offset);
}

vec4 computeSim( vec2 uv,sampler2D channel,float offset)
{


    float da=1., db=.5;

  //  float f=.014, k=.047;
  //  float f=0.022, k=0.0509;

    float f=.01, k=.042;


        float a=texture(channel,uv).x;
        float b=texture(channel,uv).y;

        float an,bn=0.;
        int s=0;
        for(float i=-offset;i<=offset;i+=offset){
            for(float j=-offset;j<=offset;j+=offset){
                vec4 c=texture(channel,uv+vec2(i,j));
                if(i==0. && j==0.)
                    c*=-1.;
                else if(abs(i)==abs(j))
                    c*=.05;
                else
                    c*=.2;
                an+=c.x;
                bn+=c.y;

            }
        }

       a=a+(da*an-(a*b*b)+f*(1.-a))*1.;
       b=b+(db*bn+(a*b*b)-(k+f)*b)*1.;


 return vec4(a,b,0.,1.);






}
