// See "FILTER SECTION" of "Buffer A" for filtering algos.
//
// All testing code (C) 2021, Charles Lohr, Use under MIT license.
// Optionally can use under CC0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
    // Normalized pixel coordinates (from 0 to 1)
    vec2 Coord0 = vec2( 0.5, 0 )/iResolution.xy;
    vec2 Coord1 = vec2( 1.5, 0 )/iResolution.xy;
    vec2 Coord2 = vec2( 2.5, 0 )/iResolution.xy;
    vec2 Coord3 = vec2( 3.5, 0 )/iResolution.xy;
    vec2 Coord4 = vec2( 4.5, 0 )/iResolution.xy;
    vec2 Coord5 = vec2( 5.5, 0 )/iResolution.xy;

    vec2 b0 = texture(iChannel0, Coord0).rg;
    vec2 b1 = texture(iChannel0, Coord1).rg;
    vec2 b2 = texture(iChannel0, Coord2).rg;
    vec2 b3 = texture(iChannel0, Coord3).rg;
    vec2 a  = texture(iChannel0, Coord4).rg;
    vec4 internal = texture(iChannel0, Coord5);

    vec2 uvtrue = fragCoord.xy / iResolution.xy;
	vec2 uv = fragCoord.xy / iResolution.yy;
    vec2 uvorig = uv;
    vec3 col = vec3( 0.0 );

    const vec3 RingColors[4] = vec3[4](
        vec3( 0.0, 0.8, 0.8 ),
        vec3( 0.8, 0.0, 0.8 ),
        vec3( 0.8, 0.8, 0.0 ),
        vec3( 0.0, 1.0, 0.0 ) );


    col += max( 0.0, (0.01 - length( (uv.xy - a) ) ) * 200. ) * vec3( 1.0 );
    col += max( 0.0, (0.01 - abs( length( (uv.xy - b0) ) - .05 ) ) * 200. ) * RingColors[0];

    if( internal.z < 0.5 )
        uv.y -= 0.25;

    col += max( 0.0, (0.01 - length( (uv.xy - a) ) ) * 200. ) * vec3( 1.0 );
    col += max( 0.0, (0.01 - abs( length( (uv.xy - b1) ) - .05 ) ) * 200. ) * RingColors[1];

    if( internal.z < 0.5 )
        uv.y -= 0.25;

    col += max( 0.0, (0.01 - length( (uv.xy - a) ) ) * 200. ) * vec3( 1.0 );
    col += max( 0.0, (0.01 - abs( length( (uv.xy - b2) ) - .05 ) ) * 200. ) * RingColors[2];

    if( internal.z < 0.5 )
        uv.y -= 0.25;

    col += max( 0.0, (0.01 - length( (uv.xy - a) ) ) * 200. ) * vec3( 1.0 );
    col += max( 0.0, (0.01 - abs( length( (uv.xy - b3) ) - .05 ) ) * 200. ) * RingColors[3];

    float edge = 1.1;//float(iResolution.x)/float(iResolution.y);
    if( uv.x > edge )
    {
        vec2 uvtarg = (uvorig - vec2( edge, 0.92 )) * 1.;
        ivec4 lines[16] = ivec4[16]( 
            ivec4( 17, 0, _E, _U ),
            ivec4( _R, _O, 0, 0 ),
            ivec4( 0, 0, 0, 0 ),
            ivec4( 0, 0, 0, 0 ),

            ivec4( _A, _D, _A, _P ),
            ivec4( _T, 0, 0, 0 ),
            ivec4( 0, 0, 0, 0 ),
            ivec4( 0, 0, 0, 0 ),

            ivec4( _F, _A, _S, _T ),
            ivec4( 0, 0, 0, 0 ),
            ivec4( 0, 0, 0, 0 ),
            ivec4( 0, 0, 0, 0 ),

            ivec4( _S, _L, _O, _W ),
            ivec4( 0, 0, 0, 0 ),
            ivec4( 0, 0, 0, 0 ),
            ivec4( 0, 0, 0, 0 ) );

        ivec2 cell = ivec2(floor(uvtarg*vec2(64.,-64)/vec2(6.,8.)));
        vec2 px = mod(uvtarg*64.,vec2(6.,8.));
        //col = vec3( (uvtarg*10.0), 0.0 );
        // 
        if( cell.y >= 0 )
        {
            col += char5x7( lines[cell.x/4+cell.y*2][cell.x%4], px ) * RingColors[3-cell.y/2];
        }
        //col = vec3(float(cell.y)/10.);
    }

    if(  uvtrue.y > .95 && uvtrue.x < 0.4 )
    {
        ivec4 lines[5] = ivec4[5]( 
            ivec4( 0, _D, _r, _a ),
            ivec4( _g,  0, _i, _n ),
            ivec4(  0, _t, _h,  _i ),
            ivec4( _s, 0, _a, _r ),
            ivec4( _e, _a, 0, 0 ) );

        ivec2 cell = ivec2(floor(uvorig*vec2(160.)/vec2(6.,8.)));
        vec2 px = mod(uvorig*160.,vec2(6.,8.));
        col += char5x7( lines[cell.x/4][cell.x%4], px );
    }

    if( uvtrue.x > 0.93 )
    {
        vec2 muv = (uvtrue - vec2( 0.93, 0 ))*14.;
        ivec4 fps_label = ivec4( _F, _P, _S, 0 );
        ivec4 drag_label = ivec4( _D, _r, _a, _g );
        ivec2 cell = ivec2(floor(muv*vec2(3.,1.)));
        vec2 px = mod(muv*vec2(6.*3.,8.),vec2(6.,8.));


        col += vec3(0.1);
        if( muv.y < 1. )
            col += print5x7int( int(1./internal.r), muv, 3, -31 );
        else if( muv.y < 2. )
            col += char5x7( fps_label[cell.x], px );
        else if( muv.y < 7. && muv.y > 3. && cell.x < 2 && cell.x > 0 )
        {
            col += char5x7( drag_label[6-cell.y], px );
        }
    }


    fragColor = vec4( col, 1.0 );
}

// All testing code (C) 2021, Charles Lohr, Use under MIT license.
// Optionally can use under CC0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragCoord -= 0.5;

    //Generate random noise.
    vec2 a = hash21( iTime * 10. ) * 0.1 + ((fract(iTime/4.0)>0.5)?vec2(0.9,0.0):vec2(0.0)) + 0.05;

    vec2 Coord0 = vec2( 0.5, 0 )/iResolution.xy;
    vec2 Coord1 = vec2( 1.5, 0 )/iResolution.xy;
    vec2 Coord2 = vec2( 2.5, 0 )/iResolution.xy;
    vec2 Coord3 = vec2( 3.5, 0 )/iResolution.xy;
    vec2 Coord4 = vec2( 4.5, 0 )/iResolution.xy;
    vec2 Coord5 = vec2( 5.5, 0 )/iResolution.xy;

    // Slow IIR
    vec2 b0 = texture(iChannel0, Coord0).rg;

    // Fast IIR
    vec2 b1 = texture(iChannel0, Coord1).rg;

    // Adaptive Filter
    vec2 b2 = texture(iChannel0, Coord2).rg;

    // Tricky: 1 Euro needs two states per axis. 
    vec4 b3 = texture(iChannel0, Coord3).rgba;

    vec4 internal = texture(iChannel0, Coord5).rgba;

    bool follow = false;
    if( iMouse.z > 0.0 )
    {
        vec2 mouseuv = iMouse.xy / iResolution.xy;
        if( mouseuv.x > .93 )
        {
            internal.r = (1./mouseuv.y)/100.;
        }
        else
        {
            follow = true;
            a = mouseuv * (iResolution.xy/iResolution.yy);
        }
    }


    if( internal.r < 0.001 ) internal.r = 0.016666;
    internal.b = follow?1.:0.;
    internal.g += iTimeDelta;

    if( internal.g >= internal.r )
    {
        float dt = internal.r;
        internal.g = min( 1.0, internal.g - internal.r );

        //FILTER SECTION

        // Slow response IIR (sluggish)
        b0 = mix( b0, a, 1.-pow(.6, dt*10.) );

        // Fast response IIR (noisy)
        b1 = mix( b1, a, 1.-pow(.04, dt*10.) );

        // Adaptive Lerp https://twitter.com/evil_arev/status/1128062338156900353
        b2 = mix( b2, a, abs(a-b2) );

        // 1€ filter https://hal.inria.fr/hal-00670496/document
        float rate = 1.0/dt;
        float dcutoff = 1.0;
        float mincutoff = 1.0;
        float beta = 1.0;
        #define ONE_EURO_ALPHA( cutoff ) (1.0/(1.0+(1.0/(2.0*3.14159*cutoff)*rate)))

        float alphad = ONE_EURO_ALPHA( dcutoff );
        vec2  dx = (a - b3.rg)*rate;
        b3.ba = mix( b3.ba, dx, alphad );  // Store the new derivative
        vec2 cutoff = mincutoff + beta * abs(b3.ba);
        vec2 alphav = ONE_EURO_ALPHA( cutoff );
        b3.rg = mix( b3.rg, a, alphav );   // Store the new value. 
    }

    if( fragCoord.x < 0.5 )
    {
        fragColor = vec4( b0, 0, 0 );
    }
    else if( fragCoord.x < 1.5 )
    {
        fragColor = vec4( b1, 0, 0 );
    }
    else if( fragCoord.x < 2.5 )
    {
        fragColor = vec4( b2, 0, 0 );
    }
    else if( fragCoord.x < 3.5 )
    {
        fragColor = vec4( b3 );
    }
    else if( fragCoord.x < 4.5 )
    {
        fragColor = vec4( a, 0, 0 );
    }
    else if( fragCoord.x < 5.5 )
    {
        fragColor = internal;
    }
    else
    {
        fragColor = vec4( 0.1, 0.1, 0.0, 0. );
    }
}

//https://www.shadertoy.com/view/4djSRW

//----------------------------------------------------------------------------------------
//  2 out, 1 in...
vec2 hash21(float p)
{
	vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
	p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}



