bool dentroCuadrado(vec2 centro, float ancho, vec2 fragCoord)
{
    return abs(fragCoord.x - centro.x) < ancho / 2.0 &&
        abs(fragCoord.y - centro.y) < ancho / 2.0;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    bool dentro = false;
    for (float x = 0.0; x < 2000.0; x = x + 100.0)
    {
        for (float y = 0.0; y < 2000.0; y = y + 100.0)
        {
            dentro = dentro || dentroCuadrado(vec2(x, y), 50.0, fragCoord);
        }
    }

    if (dentro)
        fragColor = vec4(vec3(1.0, 0.5, 0.25), 1.0);
    else
        fragColor = vec4(vec3(0.25, 0.5, 1.0), 1.0);

}
