const float PI = 3.14159;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;
    vec2 mob = vec2(0., sin(iTime * 0.15) * 0.1);
    vec2 mob2 = vec2(0., 0.); //vec2(0., sin(iTime * 0.2) * 0.15);
    float len = length(uv - mob);
    vec2 m = (iMouse.xy - .5 * iResolution.xy) / iResolution.y;
    float r = 0.1 + 0.3 * 0.5 * (1. + sin(iTime * 0.7));
    float len2 = length(uv - mob2);
    float frq = 1. + 15. * 0.5 * (1. + sin(iTime * 0.05));
    float th = 0.;//atan(uv.y, uv.x);
    float ft = 0.5 * (1. + sin(iTime * 0.53 + th));

    float ft2 = 0.5 * (1. + sin(-iTime * 1.3 + th));
    float f = pow(0.5 * (1. + sin(len * 2. * PI * frq + iTime * 0.3)), 1. + 10. * ft);
    float f2 = pow(0.5 * (1. + sin(len2 * 2. * PI * frq + iTime)), 1. + 10. * ft2);
    float flen = pow(len, 0.5);
    vec3 col = vec3(0.9 * flen - (f + f2), 0.7 - (f + f2), 0.4 * ft - (f + f2));
    fragColor = vec4(col,1.0);

}
