void mainImage(out vec4 Normal, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float scale = .10;
    vec2 step = 1. / iResolution.xy;

    float height = texture(iChannel0, uv).r;

    vec2 dxy = height - vec2(
      texture(iChannel0, uv + vec2(step.x, 0.)).r,
      texture(iChannel0, uv + vec2(0., step.y)).r
    );

    vec3 N = vec3(vec4(normalize(vec3(dxy * scale / step, 1.)), height).rgb * .5 + .5);
    Normal = vec4(N, .1);
    //fragColor = vec4(bumpFromDepth(uv, iResolution.xy, .1).rgb * .5 + .5, 1.);
}
