 const float sunRadius = 0.3;
 const float sunBlur = 0.1; // never greater than radius
 const float buildingFrequency = 20.;
 const float buildingHeight = .3;
 const float buildingWidth = .3;
 const float buildingOffset = 100.;
 const float gradientHeight = .5;
 const vec3 sunColorDay = vec3(1,1,0);
 const vec3 sunColorNight = vec3(1,0,.25);
 const vec3 skyColorDay = vec3(1,.2,.25);     
 const vec3 skyColorNight = vec3(.1,0.05,0.2);
 const vec3 buildingColor = vec3(0,0,0);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    //correct uv.x for with resolution so 
    vec2 uv = fragCoord/iResolution.xy;
    float aspectRatio = iResolution.x/ iResolution.y;
    uv.x = uv.x * aspectRatio;


    //calculations for sun
    vec2 diff = uv - vec2(0.5 * aspectRatio ,sin(iTime) * .5);
    float dist = length(diff);
    dist = (sunRadius, dist);
    vec3 color = vec3(smoothstep(sunRadius,sunBlur,dist));
    vec3 sunColor = vec3(mix(sunColorNight,sunColorDay,uv.y ));


    //gradient colors and position
     vec3 gradientColor = vec3(mix(skyColorNight, skyColorDay, sin(iTime) - uv.y + gradientHeight));


    //draw sun and gradient
    vec3 endColor = vec3(mix(gradientColor.x,sunColor.x,color.x), mix(gradientColor.y,sunColor.y,color.y), mix(gradientColor.z,sunColor.z,color.z));



    //building calculations 
    float buildings = uv.y +  step(buildingWidth, sin(uv.x *buildingFrequency + buildingOffset)) ;
    float  t = step(buildings, buildingHeight);


    //draw buildings
   endColor = mix(endColor, buildingColor, t);






    // Output to screen
    fragColor = vec4(endColor,1.0);
}
