//"xor"
//Thanks fenix and FabriceNeyret2 for further golfing

#define x(o,r) length(o - (r) * clamp( dot(o,r) / dot(r,r), 0.,1.))

void mainImage(out vec4 o, vec2 i)
{
	vec2 X = iResolution.xy,
         O = vec2(.8,0),
         R = (i+i-X)/X.y-O;

	o = (.1 - min(min( x( abs(R+O+O), O-O+.4),   //X                                      
                       abs(x(R+O*O, O-O) -.4)),  //O
                  min( x( R - O.yx/4.,  .2+O/4.),//-
                       x( R.yx+O*.5, O     ))    //|
        )        ) *X.yyyy/4.;
}



