float f (float a, float b) {
    return 0.*a*b*b+(a-0.5)*(a-0.5)+(b-0.5)*(b-0.5)-0.25;
}

vec2 mul(vec2 v, vec2 w){

    return vec2(v.x*w.x-v.y*w.y,v.x*w.y+v.y*w.x);
}

vec2 div(vec2 w, vec2 z) {
    return mul(w,vec2(z.x/(z.x*z.x+z.y*z.y),-z.y/(z.x*z.x+z.y*z.y)));
}

float absc(vec2 z){
    return sqrt(z.x*z.x+z.y*z.y);
}

vec2 f2(vec2 z) {
    return mul(z,mul(z,z))-mul(z,z)+z-vec2(1.,0.);
}

vec2 fd2(vec2 z){
    return vec2(3.,3.)*mul(z,z)-2.*z+vec2(1.,0.);
}

vec2 f3(vec2 z,vec2 x0,vec2 x1,vec2 x2){
    return mul(z,mul(z,z))
    -mul((x0+x1+x2),mul(z,z))
    +mul((mul(x0,x1)+mul(x1,x2)+mul(x2,x0)),z) - mul(x0,mul(x1,x2));
}

vec2 fd3 (vec2 z,vec2 x0,vec2 x1,vec2 x2){
    return 3.*mul(z,z)-2.*mul((x0+x1+x2),z)+(mul(x0,x1)+mul(x1,x2)+mul(x2,x0));
}

vec2 f4(vec2 z, vec2 x[10]){
    vec2 res = z-x[0];
    for (int i = 1; i <10; i++){
        res = mul(z-x[i],res);
    }
    return res;
}


vec2 fd4 (vec2 z, vec2 x[10]){
    vec2 res = z-x[9]+z-x[8];
    for (int i = 7; i >= 0; i--){
        res = mul(z-x[i],res);
        vec2 res2 = z-x[i+1];
        for (int j = i+2; j<= 9; j++){
         res2 = mul(z-x[j],res2);   
        }
        res = res+res2;
    }
    return res;
}

