void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    precision highp float;

    float time = iTime;
    vec2 mouse = iMouse.xy;
    vec2 resolution = iResolution.xy;

	const int n =       25;
	float size =       0.1;
	float width =      0.8;
	float height =      1.;
	float speed =     0.15;
	const int len =     20;
	float det =     0.0007;
    float seedx =     3.01;

	width/=2.;
	height/=2.;
	vec2 uv = ( fragCoord.xy / resolution.xy );
	float ratio = resolution.x/resolution.y;
	vec3 col = 0.5 + 0.5*cos(time+uv.xyx+vec3(0,2,4));
	float dis = 1.0;
	vec2 pos;
	uv.x *= ratio;

	for (int i=0;i<n;i++){
        for (int j=0;j<len;j++){
            pos.x=sin(float(i)*seedx)*width+0.5;
            pos.y=(float(i)/float(n))*abs(pow(cos(float(len)*det*float(j)+float(i)*12.8+time*speed*(1.+float(i)/111.)),3.))*height*2.;
            pos.x*=ratio;
            dis *= step(size,length(uv-pos));		
        }
    }
    if (mod(float(int(time/10.)),2.)==0.){
        fragColor = vec4( col, 1.0 )*(1.-dis);
    }
    else {
        fragColor = vec4( col, 1.0 )*dis;
    }

}
