float weirdness = 3.0;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Grab a pixel from the noise image
    vec4 noise = texture(iChannel1, vec2(iTime / 30.0, uv.y));

    // Get a sin vertical function
    float shift = (clamp(sin((iTime / 20.0)), 0.5, 1.0) * (((noise.x + noise.y + noise.z) / weirdness)) * 360.0 * (weirdness > 0.0 ? 1.0 : 0.0));
    float xshift = (shift > 0.0 ? sin(uv.y * 360.0) / shift : 0.0);

    noise = xshift > 0.0 ? noise : vec4(0.0, 0.0, 0.0, 0.0);

    // Separate r, g, b a bit
    vec4 scol = texture(iChannel0, uv + vec2(0.01 + xshift + noise.x / 100.0, 0.01));
    vec4 scol1 = texture(iChannel0, uv + vec2(xshift + noise.y / 100.0, 0));
    vec4 scol2 = texture(iChannel0, uv - vec2(0.01 - xshift - noise.z / 100.0, 0.01));
    float a = texture(iChannel0, uv).z;

    // Output to screen
    fragColor = vec4(scol.x, scol1.y, scol2.z, a);
}
