void mainImage( out vec4 O, vec2 U )
{
    U = floor(32.*U/iResolution.xy);
    ivec2 I = ivec2(U);

    O = vec4( min(-1,iFrame)%I.y==I.x);  // min(-1,iFrame) = not const( -1 )

    O.b  += .4*float(mod(-1.,U.y)==U.x); // blue reference with mod()
    O.rg += .2*vec2(I%2);                // checker
}
