/////////////////////////////////////////////////////////////
//                    ... Aura Video ...                 ////
/////////////////////////////////////////////////////////////
// Brasil/Amazonas/Manaus
// Created by Rodrigo Cal (twitter: @rmmcal)
// - Published: 2021/12
// https://www.shadertoy.com/view/NldXD8
//
// (fwidth) Inspired by @Xor https://www.shadertoy.com/view/slcSW8
//
/////////////////////////////////////////////////////////////
void mainImage( out vec4 O, vec2 I )
{
    O = vec4(0.0);
    vec2 uv = I/iResolution.xy;
    for(float i=0.0; i++ < 6.0;)
    {
      float v = exp2(i+2.0);
      O += (texture(iChannel0, uv,4.0+i) );
    }
    O -= (texture(iChannel0, uv,0.0).a);
}

/////////////////////////////////////////////////////////////
//                    ... Aura Video ...                 ////
/////////////////////////////////////////////////////////////
// Brasil/Amazonas/Manaus
// Created by Rodrigo Cal (twitter: @rmmcal)
// - Published: 2021/12
// https://www.shadertoy.com/view/NldXD8
// 
// (fwidth) Inspired by @Xor https://www.shadertoy.com/view/slcSW8
// 
/////////////////////////////////////////////////////////////
void mainImage( out vec4 O, vec2 I )
{
    O = vec4(0.0);
    vec2 uv = I/iResolution.xy;
    float k = 1.;
    for (float i = 0.0; i < 10.0; i++)
      k *= (cos(iTime*i*3.0+cos(iTime*(-i+3.0)))*0.1+.9);

    for(float i=0.0; i++ < 16.0;)
      O += fwidth(texture(iChannel0, uv,i*2.0) )*exp2(i+2.0)*4.0*k;

    O.a = length(texture(iChannel0, uv).rgb );
}
