vec4 char(vec2 p, int c) { // from https://www.shadertoy.com/view/llySRh
    vec2 dFdx = dFdx(p/16.), dFdy = dFdy(p/16.);
    if (p.x<.0|| p.x>1. || p.y<0.|| p.y>1.) return vec4(0,0,0,1e5);
	return textureGrad( iChannel3, p/16. + fract( vec2(c, 15-c/16) / 16. ), 
                        dFdx, dFdy );
}
vec4 pF11(vec2 p, float n) {
    vec4 v = char(p, 48+ int(n) );
         p.x -= .3;
         v += char(p, 46);
         p.x -= .3;
         v += char(p, 48+ int(fract(n)*10.) );
    return v;
}
void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, U = u/R;
    int   x = int( 8. * U.x );
    float g = length(iMouse.xy)<10. ? 2.2 : 1. + 2.*iMouse.y/R.y,
          y = pow(U.y,1./g);
    float n = step(texelFetch(iChannel0, ivec2(u)%1024, 0 ).r,U.y);
    O-=O;
    if (x<2) O = vec4( x==0 ? n : y) ;
    else     O[x/2-1] = x%2==0 ? n : y;

    O = mix(O, vec4(1), pF11(3.*(2.*u-R)/R.y,g).r );
}
